import React,{useState} from "react";
import {StyleSheet,View,Text,TextInput,Button} from "react-native";
import {Formik} from "formik";
import * as yup from"yup";
import {RadioButton} from "react-native-paper"
const ReviewSchema=yup.object({
    otp:yup.string().required().min(2).max(10),
});
export default function Otp({navigation})
{
  // outputvalue
    const [otp, setotp] = useState("....");
  const handleChange = (val, index) => {
    console.log("value passed--->",navigation.state.params);
    let newotp = otp;
    newotp = newotp.substring(0, index) + val + newotp.substring(index + 1, 4);
    setotp(newotp);
    console.log(otp);
  };
    const onSubmitHandler=()=>{
        console.log("otp->>>>>",otp);
        console.log("going to other page");
        navigation.push('Password',navigation.state.params);
    }
    
    return (
        <View>
         
            <View style={styles.otp}>
          <TextInput
            style={styles.value}
            keyboardType={"decimal-pad"}
            maxLength={1}
            onChangeText={(val) => {
              handleChange(val, 0);
            }}
          />
          <TextInput
            style={styles.value}
            keyboardType={"decimal-pad"}
            maxLength={1}
            onChangeText={(val) => {
              handleChange(val, 1);
            }}
          />
          <TextInput
            style={styles.value}
            keyboardType={"decimal-pad"}
            maxLength={1}
            onChangeText={(val) => {
              handleChange(val, 2);
            }}
          />
          <TextInput
            style={styles.value}
            keyboardType={"decimal-pad"}
            maxLength={1}
            onChangeText={(val) => {
              handleChange(val, 3);
            }}
          />
        </View>
        <Button title='submit otp' onPress={onSubmitHandler}/>
    </View>
    );
} 
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    radio:{
      flexDirection:'row',
    },
    radio1:{
      flexDirection:'row'
    },
    otp: {
        alignItems: "center",
        justifyContent: "space-between",
        flexDirection: "row",
      },
      value: {
        width: "20%",
        marginBottom: 10,
        paddingLeft: 10,
        borderBottomWidth: 1,
      },
  });
