import React, { useState } from "react";
import {StyleSheet,View,Text,TextInput,Button} from "react-native";
import {Formik} from "formik";
import * as yup from"yup";
import {RadioButton} from "react-native-paper"
const ReviewSchema=yup.object({
    username:yup.string().required(),
    password:yup.string().required(),
});
export default function Login({navigation})
{
    const [loginDetails,setloginDetails]=useState({'username':'','password':''});
    const onSubmitHandler=(val)=>{
        setloginDetails({
           'username':val.username,
           'password':val.password, 
        })
        console.log("going to other page",val);
        navigation.push("Homescreen");
    }
    const [value, setValue] = React.useState(1);
    return (
        <View>
        <Formik initialValues={{username:'',password:''}}
            validationSchema={ReviewSchema}
            onSubmit={(values,actions)=>{
                // addreview(values);
                console.log(values);
                actions.resetForm();
                
            }}>
                {(props)=>(
                    <View >
                        <Text>username</Text>
                        <TextInput  placeholder='username' value={props.values.username} onChangeText={props.handleChange('username')}/>
                        <Text>{props.touched.username && props.errors.username}</Text>
                        <Text>password</Text>
                        <TextInput  placeholder='password' value={props.values.password} onChangeText={props.handleChange('password')}/>
                        <Text>{props.touched.password && props.errors.password}</Text>
                        <Button  title="Login" onPress={()=>{props.handleSubmit();onSubmitHandler(props.values)}}/>
                    </View>
                )}
        </Formik>
    </View>
    );
} 
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    radio:{
      flexDirection:'row',
    },
    radio1:{
      flexDirection:'row'
    }
  });
