import React,{useState} from "react";
import {StyleSheet,View,Text,TextInput,Button} from "react-native";
import {Formik} from "formik";
import * as yup from"yup";
import {RadioButton} from "react-native-paper"
import axios from 'axios';
import {mobileLink} from '../links';
const ReviewSchema=yup.object({
    otp:yup.string().required().min(2).max(10),
});
export default function Otp({navigation})
{
  // outputvalue
    const [otp, setotp] = useState("....");
  const handleChange = (val, index) => {
    console.log("value passed--->",navigation.state.params);
    let newotp = otp;
    newotp = newotp.substring(0, index) + val + newotp.substring(index + 1, 4);
    setotp(newotp);
    console.log(otp);
  };
    const onSubmitHandler=async ()=>{
        console.log("otp->>>>>",otp);
        const params = JSON.stringify({
          phone_number: navigation.state.params.phone_number,
          otp:otp
        });
    
        let axiosConfig = {
          headers: {
            "Content-Type": "application/json;charset=UTF-8",
          },
        };
    
    
        
        const response = await axios.post(`${mobileLink}validateotp/`, params,axiosConfig);
            console.log(response);



        console.log("going to other page",navigation.state.params);

       // navigation.push('Password',navigation.state.params.value);
    }
    
    return (
        <View>
         
            <View style={styles.otp}>
          <TextInput
            style={styles.value}
            keyboardType={"decimal-pad"}
            maxLength={1}
            onChangeText={(val) => {
              handleChange(val, 0);
            }}
          />
          <TextInput
            style={styles.value}
            keyboardType={"decimal-pad"}
            maxLength={1}
            onChangeText={(val) => {
              handleChange(val, 1);
            }}
          />
          <TextInput
            style={styles.value}
            keyboardType={"decimal-pad"}
            maxLength={1}
            onChangeText={(val) => {
              handleChange(val, 2);
            }}
          />
          <TextInput
            style={styles.value}
            keyboardType={"decimal-pad"}
            maxLength={1}
            onChangeText={(val) => {
              handleChange(val, 3);
            }}
          />
        </View>
        <Button title='submit otp' onPress={onSubmitHandler}/>
    </View>
    );
} 
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    radio:{
      flexDirection:'row',
    },
    radio1:{
      flexDirection:'row'
    },
    otp: {
        alignItems: "center",
        justifyContent: "space-between",
        flexDirection: "row",
      },
      value: {
        width: "20%",
        marginBottom: 10,
        paddingLeft: 10,
        borderBottomWidth: 1,
      },
  });
