import React, { useEffect, useState } from "react";
import { StyleSheet, View, Text, TextInput, Button, As } from "react-native";
import { Formik } from "formik";
import * as yup from "yup";
import { RadioButton } from "react-native-paper";
import axios from "axios";
import {mobileLink} from '../links'
const ReviewSchema = yup.object({
  phonenumber: yup.string().required().min(2).max(10),
});
export default function Register({ navigation }) {
  // output value
  const [registerData, setregisterData] = useState({
    phone: "",
    typeofuser: 1,
  });
  const [value, setValue] = React.useState(1);

  const onSubmitHandler =async (val) => {
    console.log(val.phonenumber, " -------  ", value);

    const params = JSON.stringify({
      phone_number: val.phonenumber,
    });

    let axiosConfig = {
      headers: {
        "Content-Type": "application/json;charset=UTF-8",
      },
    };


    
    const response = await axios.post(`${mobileLink}sendotp/`, params,axiosConfig);
        console.log(response);
    console.log("chechk update----->", registerData);
    navigation.push("Otp", { 'value':value, 'phone_number':val.phonenumber});
  };

  const loginpage = () => {
    navigation.push("Login");
  };

  return (
    <View style={styles.container}>
      <View style={styles.container1}></View>
      
      <View style={styles.container3}>
        <Formik initialValues={{phonenumber:''}} validationSchema={ReviewSchema} onSubmit={(values,actions)=>{actions.resetForm();}}>
          {(props)=>(
            <View style={styles.container-3-1}>
              <Text style={styles.container311}>Enter Mobile Number*</Text>
              <TextInput style={styles.container312} keyboardType={"decimal-pad"}  placeholder='10 Digit Mobile Number' value={props.values.phonenumber} onChangeText={props.handleChange('phonenumber')}/>
              {/* <Text>{props.touched.title && props.errors.title}</Text> */}
              <Button  title="REGISTER" color="#FC8019"  onPress={()=>{props.handleSubmit();onSubmitHandler(props.values)}}/>
            </View>
          )}
        </Formik>
      </View>
      <View style={styles.container4}>
        <Button title='already registered proceed to login' color="#FC8019" onPress={loginpage}/>
        <Button title="next"   onPress={()=>{navigation.push('Otp')}}/>
      </View>
    </View>
  );
}
const styles = StyleSheet.create({
  container:{
    flexDirection:'column',
    justifyContent:'flex-start',
    backgroundColor:'#ffffff',
  },
