import React,{useState} from "react";
import {StyleSheet,View,Text,TextInput,Button} from "react-native";
import {Formik} from "formik";
import * as yup from"yup";
import {RadioButton} from "react-native-paper";
const ReviewSchema=yup.object({
    fullname:yup.string().required().min(2).max(10),
    emailid:yup.string().required(),
    
});
export default function UserDetails({navigation})
{
  const [userDetails,setuserDetails]=useState({'fullname':'','emailid':'','gender':1});
  const [value, setValue] = React.useState(1); 
  
  

  const onSubmitHandler=(val)=>{
      setuserDetails({
        'fullname':val.fullname,
        'emailid':val.emailid,
        'gender':value
      })
      let gender="O"
        if(value===1)
          gender="F";
        if(value===2)
          gender="M"
        console.log(navigation.state.params);
        console.log("going to other page");
        navigation.push('Location',{"phone_number":navigation.state.params.phone_number,
        "type":navigation.state.params.value,"fullname":val.fullname,"emailid":val.emailid,
        "gender":gender});
    }
    
    
    return (
        <View>
            <Formik initialValues={{fullname:'',emailid:''}}
            validationSchema={ReviewSchema}
            onSubmit={(values,actions)=>{
                // addreview(values);
                console.log(values);
                actions.resetForm();
                
            }}>
                {(props)=>(
                    <View >
                        <Text>Enter Full Name</Text>
                        <TextInput  placeholder='Enter Full Name' value={props.values.fullname} onChangeText={props.handleChange('fullname')}/>
                        <Text>{props.touched.fullname && props.errors.fullname}</Text>
                        <Text>Email Id</Text>
                        <TextInput  placeholder='Email Id' value={props.values.emailid} onChangeText={props.handleChange('emailid')}/>
                        <Text>{props.touched.emailid && props.errors.emailid}</Text>
                       
                        <RadioButton.Group onValueChange={newValue => setValue(newValue)} value={value} >
    <View style={styles.radio}>
      <View style={styles.radio1}>
        
        <RadioButton title="scasc1"value={1} />
        <Text>female</Text>
      </View>
      <View style={styles.radio1}>
        
        <RadioButton value={2} />
        <Text>male</Text>
      </View>
      <View style={styles.radio1}>
        
        <RadioButton value={3} />
        <Text>other</Text>
      </View>
      </View>
    </RadioButton.Group>
                        <Button  title="Continue" onPress={()=>{props.handleSubmit();onSubmitHandler(props.values)}}/>
                    </View>
                )}
        </Formik>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    radio:{
      flexDirection:'row',
    },
    radio1:{
      flexDirection:'row'
    }
  });
